package com.hand.push.impl;import java.io.InputStream;import java.util.List;import com.hand.push.core.NotificationPush;import com.hand.push.core.Pusher;import com.hand.push.domain.UserPushToken;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Component("PushCenter")public  class PushCenter implements NotificationPush {	private  List<Pusher> pushers;		@Autowired	public void setPusher(List<Pusher> pushers) {		this.pushers = pushers;	}	@Override	public void putRecord(UserPushToken ut) {		for (Pusher pusher : pushers) {			if(pusher.tellMeYourDeviceType().equalsIgnoreCase(ut.getDeviceType()))				pusher.putRecord(ut);					InputStream is = null;		}	}		}